import React from 'react';
import { colors, typography, spacing, borderRadius } from '../styles/theme';

interface ButtonProps {
  children: React.ReactNode;
  onClick?: () => void;
  disabled?: boolean;
  variant?: 'primary' | 'secondary' | 'success' | 'warning' | 'danger' | 'edit' | 'transfer';
  size?: 'sm' | 'md';
  loading?: boolean;
  type?: 'button' | 'submit';
}

const variantStyles = {
  primary: {
    backgroundColor: colors.primary,
    hoverColor: '#4338ca'
  },
  secondary: {
    backgroundColor: colors.text.secondary,
    hoverColor: '#4b5563'
  },
  success: {
    backgroundColor: '#10b981',
    hoverColor: '#059669'
  },
  warning: {
    backgroundColor: '#f59e0b',
    hoverColor: '#d97706'
  },
  danger: {
    backgroundColor: '#ef4444',
    hoverColor: '#dc2626'
  },
  edit: {
    backgroundColor: '#3b82f6',
    hoverColor: '#2563eb'
  },
  transfer: {
    backgroundColor: '#f59e0b',
    hoverColor: '#d97706'
  }
};

const sizeStyles = {
  sm: {
    padding: '4px 8px',
    fontSize: '11px'
  },
  md: {
    padding: `${spacing.sm} ${spacing.md}`,
    fontSize: typography.sm
  }
};

export const Button: React.FC<ButtonProps> = ({
  children,
  onClick,
  disabled = false,
  variant = 'primary',
  size = 'md',
  loading = false,
  type = 'button'
}) => {
  const variantStyle = variantStyles[variant];
  const sizeStyle = sizeStyles[size];

  return (
    <button
      type={type}
      onClick={onClick}
      disabled={disabled || loading}
      style={{
        padding: sizeStyle.padding,
        backgroundColor: disabled || loading ? '#9ca3af' : variantStyle.backgroundColor,
        color: 'white',
        border: 'none',
        borderRadius: size === 'sm' ? '4px' : borderRadius.sm,
        fontSize: sizeStyle.fontSize,
        cursor: disabled || loading ? 'not-allowed' : 'pointer',
        transition: 'background-color 0.2s',
        opacity: disabled || loading ? 0.5 : 1
      }}
      onMouseEnter={(e) => {
        if (!disabled && !loading) {
          e.currentTarget.style.backgroundColor = variantStyle.hoverColor;
        }
      }}
      onMouseLeave={(e) => {
        if (!disabled && !loading) {
          e.currentTarget.style.backgroundColor = variantStyle.backgroundColor;
        }
      }}
    >
      {loading ? 'Loading...' : children}
    </button>
  );
};
