import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { Team, Transfer, Player } from '../types';
import { transferAPI, teamAPI, playerAPI } from '../services/api';
import { CurrencyDisplay } from './CurrencyDisplay';
import { SortableHeader } from './SortableHeader';
import { Button } from './Button';
import { Modal } from './Modal';
import { cardStyle } from '../styles/common';
import { colors, typography, spacing, borderRadius } from '../styles/theme';

interface SortConfig {
  key: keyof Team['players'][0];
  direction: 'asc' | 'desc';
}

interface DashboardProps {
  team: Team;
  onTeamUpdate?: (team: Team) => void;
}

const POSITION_ORDER: Record<string, number> = {
  'GOALKEEPER': 0,
  'DEFENDER': 1,
  'MIDFIELDER': 2,
  'ATTACKER': 3,
  'goalkeeper': 0,
  'defender': 1,
  'midfielder': 2,
  'attacker': 3
};

export const Dashboard: React.FC<DashboardProps> = ({ team, onTeamUpdate }) => {
  const [sortConfig, setSortConfig] = useState<SortConfig>({
    key: 'position',
    direction: 'asc'
  });
  const [transfers, setTransfers] = useState<Transfer[]>([]);
  const [transferingPlayer, setTransferingPlayer] = useState<string | null>(null);
  const [showTransferModal, setShowTransferModal] = useState(false);
  const [selectedPlayer, setSelectedPlayer] = useState<Player | null>(null);
  const [askPrice, setAskPrice] = useState('');
  const [error, setError] = useState('');
  const [isEditMode, setIsEditMode] = useState(false);
  const [currentTransferId, setCurrentTransferId] = useState<string | null>(null);
  const [showRemoveConfirm, setShowRemoveConfirm] = useState(false);
  const [playerToRemove, setPlayerToRemove] = useState<Player | null>(null);
  const [removingPlayer, setRemovingPlayer] = useState<string | null>(null);

  // Team editing state
  const [editingTeam, setEditingTeam] = useState(false);
  const [teamForm, setTeamForm] = useState({ name: team.name, country: team.country });
  const [loading, setLoading] = useState(false);

  // Player editing state
  const [editingPlayer, setEditingPlayer] = useState<string | null>(null);
  const [playerForm, setPlayerForm] = useState<Partial<Player>>({});

  useEffect(() => {
    const fetchTransfers = async () => {
      try {
        const transfersData = await transferAPI.getTransfers();
        setTransfers(transfersData);
      } catch (err) {
        console.error('Failed to fetch transfers:', err);
      }
    };
    fetchTransfers();
  }, []);

  const getPlayerStatus = useCallback((playerId: string): 'ACTIVE' | 'ON_TRANSFER_LIST' => {
    const isOnTransferList = transfers.some(transfer => transfer.player.id === playerId);
    return isOnTransferList ? 'ON_TRANSFER_LIST' : 'ACTIVE';
  }, [transfers]);

  const playerStatuses = useMemo(() => {
    const statuses: Record<string, 'ACTIVE' | 'ON_TRANSFER_LIST'> = {};
    team.players?.forEach(player => {
      statuses[player.id] = getPlayerStatus(player.id);
    });
    return statuses;
  }, [team.players, getPlayerStatus]);

  const stats = [
    {
      label: 'Team Name',
      value: team.name,
      format: 'text'
    },
    {
      label: 'Country',
      value: team.country,
      format: 'text'
    },
    {
      label: 'Team Value',
      value: parseFloat(team.marketValue || '0'),
      format: 'currency'
    },
    {
      label: 'Budget',
      value: parseFloat(team.budget || '0'),
      format: 'currency'
    }
  ];

  const sortedPlayers = useMemo(() => {
    if (!team.players) return [];

    const sorted = [...team.players].sort((a, b) => {
      // Special handling for position sorting
      if (sortConfig.key === 'position') {
        const aPositionOrder = POSITION_ORDER[a.position] ?? POSITION_ORDER[a.position?.toLowerCase()] ?? 999;
        const bPositionOrder = POSITION_ORDER[b.position] ?? POSITION_ORDER[b.position?.toLowerCase()] ?? 999;

        return sortConfig.direction === 'asc' 
          ? aPositionOrder - bPositionOrder
          : bPositionOrder - aPositionOrder;
      }

      // Regular sorting for other fields
      const aValue = a[sortConfig.key] ?? '';
      const bValue = b[sortConfig.key] ?? '';

      if (aValue < bValue) {
        return sortConfig.direction === 'asc' ? -1 : 1;
      }
      if (aValue > bValue) {
        return sortConfig.direction === 'asc' ? 1 : -1;
      }
      return 0;
    });

    return sorted;
  }, [team.players, sortConfig]);

  const handleSort = useCallback((key: string) => {
    setSortConfig(current => ({
      key: key as keyof Team['players'][0],
      direction: current.key === key && current.direction === 'asc' ? 'desc' : 'asc'
    }));
  }, []);

  const handlePutOnTransferList = useCallback((player: Player) => {
    setSelectedPlayer(player);
    setAskPrice(player.marketValue.toString());
    setShowTransferModal(true);
    setError('');
    setIsEditMode(false);
    setCurrentTransferId(null);
  }, []);

  const handleEditTransferPrice = useCallback((player: Player) => {
    const transfer = transfers.find(t => t.player.id === player.id);
    if (transfer) {
      setSelectedPlayer(player);
      setAskPrice(transfer.askPrice);
      setShowTransferModal(true);
      setError('');
      setIsEditMode(true);
      setCurrentTransferId(transfer.id);
    }
  }, [transfers]);

  const handleRemoveFromTransferList = useCallback((player: Player) => {
    setPlayerToRemove(player);
    setShowRemoveConfirm(true);
  }, []);

  const confirmRemoveTransfer = async () => {
    if (!playerToRemove) return;

    setRemovingPlayer(playerToRemove.id);

    try {
      await transferAPI.removeTransfer(team.id, playerToRemove.id);
      const transfersData = await transferAPI.getTransfers();
      setTransfers(transfersData);
      setShowRemoveConfirm(false);
      setPlayerToRemove(null);
    } catch (err) {
      setError('Failed to remove player from transfer list');
    } finally {
      setRemovingPlayer(null);
    }
  };

  const cancelRemoveTransfer = () => {
    setShowRemoveConfirm(false);
    setPlayerToRemove(null);
  };

  const handleCreateTransfer = async () => {
    if (!selectedPlayer || !askPrice) {
      setError('Please enter a valid ask price');
      return;
    }

    setTransferingPlayer(selectedPlayer.id);
    setError('');

    try {
      if (isEditMode && currentTransferId) {
        await transferAPI.updateTransfer(team.id, currentTransferId, parseFloat(askPrice));
      } else {
        await transferAPI.createTransfer(team.id, selectedPlayer.id, parseFloat(askPrice));
      }
      const transfersData = await transferAPI.getTransfers();
      setTransfers(transfersData);
      setShowTransferModal(false);
      setSelectedPlayer(null);
      setAskPrice('');
      setIsEditMode(false);
      setCurrentTransferId(null);
    } catch (err) {
      setError(isEditMode ? 'Failed to update transfer price' : 'Failed to put player on transfer list');
    } finally {
      setTransferingPlayer(null);
    }
  };

  const handleCancelTransfer = useCallback(() => {
    setShowTransferModal(false);
    setSelectedPlayer(null);
    setAskPrice('');
    setError('');
    setIsEditMode(false);
    setCurrentTransferId(null);
  }, []);

  // Team editing functions
  const handleTeamEdit = () => {
    setEditingTeam(true);
    setTeamForm({ name: team.name, country: team.country });
  };

  const handleTeamSave = async () => {
    setLoading(true);
    setError('');
    try {
      await teamAPI.updateTeam(team.id, teamForm);
      // Refetch full team data to get updated players
      if (onTeamUpdate) {
        const updatedTeam = await teamAPI.getMyTeam(team.id);
        onTeamUpdate(updatedTeam);
      }
      setEditingTeam(false);
    } catch (err: any) {
      setError(err.response?.data?.message || "Failed to update team");
    } finally {
      setLoading(false);
    }
  };

  const handleTeamCancel = () => {
    setEditingTeam(false);
    setTeamForm({ name: team.name, country: team.country });
  };

  // Player editing functions
  const handlePlayerEdit = (player: Player) => {
    setEditingPlayer(player.id);
    setPlayerForm({
      firstName: player.firstName,
      lastName: player.lastName,
      age: player.age,
      country: player.country,
      position: player.position,
    });
  };

  const handlePlayerSave = async (playerId: string) => {
    setLoading(true);
    setError('');
    try {
      await playerAPI.updatePlayer(team.id, playerId, playerForm);
      // Refetch team to get updated player data
      if (onTeamUpdate) {
        const updatedTeam = await teamAPI.getMyTeam(team.id);
        onTeamUpdate(updatedTeam);
      }
      setEditingPlayer(null);
      setPlayerForm({});
    } catch (err: any) {
      setError(err.response?.data?.message || "Failed to update player");
    } finally {
      setLoading(false);
    }
  };

  const handlePlayerCancel = () => {
    setEditingPlayer(null);
    setPlayerForm({});
  };

  const SortableHeaderComponent = useMemo(() => ({ children, sortKey, label }: { children: React.ReactNode; sortKey: string; label: string }) => (
    <SortableHeader
      sortKey={sortKey}
      label={label}
      currentSortKey={sortConfig.key}
      sortDirection={sortConfig.direction}
      onSort={handleSort}
    >
      {children}
    </SortableHeader>
  ), [sortConfig.key, sortConfig.direction, handleSort]);

  return (
    <div style={{ display: 'flex', flexDirection: 'column', gap: spacing.lg }}>
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
        gap: spacing.lg
      }}>
        {stats.map((stat, index) => (
          <div key={index} style={{
            ...cardStyle,
            padding: spacing.lg
          }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
              <div style={{ flex: 1 }}>
                <h3 style={{
                  fontSize: typography.sm,
                  fontWeight: '500',
                  color: colors.text.secondary,
                  marginBottom: spacing.sm
                }}>
                  {stat.label}
                </h3>
                {editingTeam && (stat.label === 'Team Name' || stat.label === 'Country') ? (
                  <input
                    type="text"
                    value={stat.label === 'Team Name' ? teamForm.name : teamForm.country}
                    onChange={(e) => setTeamForm({
                      ...teamForm,
                      [stat.label === 'Team Name' ? 'name' : 'country']: e.target.value
                    })}
                    style={{
                      width: '100%',
                      padding: '8px',
                      border: `1px solid ${colors.border}`,
                      borderRadius: borderRadius.md,
                      fontSize: typography.lg,
                      fontWeight: '600'
                    }}
                  />
                ) : (
                  <p style={{
                    fontSize: typography.lg,
                    fontWeight: '600',
                    color: colors.text.primary
                  }}>
                    {stat.format === 'currency' ? (
                      <CurrencyDisplay amount={stat.value} />
                    ) : (
                      stat.value
                    )}
                  </p>
                )}
              </div>
              {(stat.label === 'Team Name' || stat.label === 'Country') && (
                <div style={{ display: 'flex', gap: spacing.xs }}>
                  {editingTeam ? (
                    <>
                      <Button
                        onClick={handleTeamSave}
                        disabled={loading}
                        variant="primary"
                        size="sm"
                        loading={loading}
                      >
                        Save
                      </Button>
                      <Button
                        onClick={handleTeamCancel}
                        variant="secondary"
                        size="sm"
                      >
                        Cancel
                      </Button>
                    </>
                  ) : (
                    <Button
                      onClick={handleTeamEdit}
                      variant="secondary"
                      size="sm"
                    >
                      Edit
                    </Button>
                  )}
                </div>
              )}
            </div>
          </div>
        ))}
      </div>

      <div style={cardStyle}>
        <div style={{
          padding: spacing.lg,
          borderBottom: `1px solid ${colors.border}`
        }}>
          <h3 style={{
            fontSize: typography.lg,
            fontWeight: '600',
            color: colors.text.primary,
            marginBottom: spacing.xs
          }}>
            Players
          </h3>
          <p style={{
            fontSize: typography.sm,
            color: colors.text.secondary
          }}>
            View your team players and their details.
          </p>
        </div>

        {sortedPlayers.length === 0 ? (
          <div style={{
            padding: spacing.xl,
            textAlign: 'center',
            color: colors.text.secondary
          }}>
            No players found
          </div>
        ) : (
          <>
            <div style={{
              overflowX: 'auto',
              border: `1px solid ${colors.border}`,
              borderRadius: borderRadius.md
            }}>
              <table style={{
                width: '100%',
                borderCollapse: 'collapse',
                backgroundColor: colors.surface
              }}>
                <thead>
                  <tr style={{
                    backgroundColor: colors.background,
                    borderBottom: `2px solid ${colors.border}`
                  }}>
                    <th style={{
                      padding: spacing.md,
                      textAlign: 'left',
                      fontSize: typography.sm,
                      fontWeight: '600',
                      color: colors.text.primary,
                      borderRight: `1px solid ${colors.border}`
                    }}>
                      <SortableHeaderComponent sortKey="firstName" label="Name">Name</SortableHeaderComponent>
                    </th>
                    <th style={{
                      padding: spacing.md,
                      textAlign: 'left',
                      fontSize: typography.sm,
                      fontWeight: '600',
                      color: colors.text.primary,
                      borderRight: `1px solid ${colors.border}`
                    }}>
                      <SortableHeaderComponent sortKey="position" label="Position">Position</SortableHeaderComponent>
                    </th>
                    <th style={{
                      padding: spacing.md,
                      textAlign: 'left',
                      fontSize: typography.sm,
                      fontWeight: '600',
                      color: colors.text.primary,
                      borderRight: `1px solid ${colors.border}`
                    }}>
                      <SortableHeaderComponent sortKey="age" label="Age">Age</SortableHeaderComponent>
                    </th>
                    <th style={{
                      padding: spacing.md,
                      textAlign: 'left',
                      fontSize: typography.sm,
                      fontWeight: '600',
                      color: colors.text.primary,
                      borderRight: `1px solid ${colors.border}`
                    }}>
                      <SortableHeaderComponent sortKey="country" label="Country">Country</SortableHeaderComponent>
                    </th>
                    <th style={{
                      padding: spacing.md,
                      textAlign: 'left',
                      fontSize: typography.sm,
                      fontWeight: '600',
                      color: colors.text.primary
                    }}>
                      <SortableHeaderComponent sortKey="marketValue" label="Market Value">Market Value</SortableHeaderComponent>
                    </th>
                    <th style={{
                      padding: spacing.md,
                      textAlign: 'center',
                      fontSize: typography.sm,
                      fontWeight: '600',
                      color: colors.text.primary
                    }}>
                      Status
                    </th>
                    <th style={{
                      padding: spacing.md,
                      textAlign: 'center',
                      fontSize: typography.sm,
                      fontWeight: '600',
                      color: colors.text.primary
                    }}>
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {sortedPlayers.map((player, index) => (
                    <tr key={player.id} style={{
                      backgroundColor: index % 2 === 0 ? colors.surface : colors.background,
                      borderBottom: `1px solid ${colors.border}`,
                      transition: 'background-color 0.2s'
                    }}>
                      <td style={{
                        padding: spacing.md,
                        fontSize: typography.sm,
                        color: colors.text.primary,
                        borderRight: `1px solid ${colors.border}`
                      }}>
                        {editingPlayer === player.id ? (
                          <div style={{ display: 'flex', flexDirection: 'column', gap: spacing.xs }}>
                            <input
                              type="text"
                              value={playerForm.firstName || ''}
                              onChange={(e) => setPlayerForm({ ...playerForm, firstName: e.target.value })}
                              placeholder="First name"
                              style={{
                                width: '100%',
                                padding: '4px',
                                border: `1px solid ${colors.border}`,
                                borderRadius: borderRadius.sm,
                                fontSize: typography.sm
                              }}
                            />
                            <input
                              type="text"
                              value={playerForm.lastName || ''}
                              onChange={(e) => setPlayerForm({ ...playerForm, lastName: e.target.value })}
                              placeholder="Last name"
                              style={{
                                width: '100%',
                                padding: '4px',
                                border: `1px solid ${colors.border}`,
                                borderRadius: borderRadius.sm,
                                fontSize: typography.sm
                              }}
                            />
                          </div>
                        ) : (
                          <div>
                            <div style={{ fontWeight: '500' }}>
                              {player.firstName} {player.lastName}
                            </div>
                          </div>
                        )}
                      </td>
                      <td style={{
                        padding: spacing.md,
                        fontSize: typography.sm,
                        color: colors.text.secondary,
                        borderRight: `1px solid ${colors.border}`
                      }}>
                        {editingPlayer === player.id ? (
                          <select
                            value={playerForm.position || ''}
                            onChange={(e) => setPlayerForm({ ...playerForm, position: e.target.value as Player['position'] })}
                            style={{
                              width: '100%',
                              padding: '4px',
                              border: `1px solid ${colors.border}`,
                              borderRadius: borderRadius.sm,
                              fontSize: typography.sm
                            }}
                          >
                            <option value="GOALKEEPER">Goalkeeper</option>
                            <option value="DEFENDER">Defender</option>
                            <option value="MIDFIELDER">Midfielder</option>
                            <option value="ATTACKER">Attacker</option>
                          </select>
                        ) : (
                          player.position
                        )}
                      </td>
                      <td style={{
                        padding: spacing.md,
                        fontSize: typography.sm,
                        color: colors.text.secondary,
                        borderRight: `1px solid ${colors.border}`
                      }}>
                        {editingPlayer === player.id ? (
                          <input
                            type="number"
                            value={playerForm.age || ''}
                            onChange={(e) => setPlayerForm({ ...playerForm, age: Number(e.target.value) })}
                            style={{
                              width: '60px',
                              padding: '4px',
                              border: `1px solid ${colors.border}`,
                              borderRadius: borderRadius.sm,
                              fontSize: typography.sm
                            }}
                          />
                        ) : (
                          `${player.age} years`
                        )}
                      </td>
                      <td style={{
                        padding: spacing.md,
                        fontSize: typography.sm,
                        color: colors.text.secondary,
                        borderRight: `1px solid ${colors.border}`
                      }}>
                        {editingPlayer === player.id ? (
                          <input
                            type="text"
                            value={playerForm.country || ''}
                            onChange={(e) => setPlayerForm({ ...playerForm, country: e.target.value })}
                            style={{
                              width: '100%',
                              padding: '4px',
                              border: `1px solid ${colors.border}`,
                              borderRadius: borderRadius.sm,
                              fontSize: typography.sm
                            }}
                          />
                        ) : (
                          player.country
                        )}
                      </td>
                      <td style={{
                        padding: spacing.md,
                        fontSize: typography.sm,
                        color: colors.text.primary,
                        borderRight: `1px solid ${colors.border}`
                      }}>
                        <CurrencyDisplay amount={player.marketValue} />
                      </td>
                      <td style={{
                        padding: spacing.md,
                        fontSize: typography.sm,
                        textAlign: 'center'
                      }}>
                        {(() => {
                          const playerStatus = playerStatuses[player.id] || 'ACTIVE';
                          return (
                            <span style={{
                              padding: '2px 8px',
                              fontSize: '12px',
                              fontWeight: '500',
                              borderRadius: '9999px',
                              backgroundColor: playerStatus === 'ACTIVE' ? '#dcfce7' : 
                                             playerStatus === 'ON_TRANSFER_LIST' ? '#fef3c7' : '#e5e7eb',
                              color: playerStatus === 'ACTIVE' ? '#166534' : 
                                      playerStatus === 'ON_TRANSFER_LIST' ? '#92400e' : '#6b7280'
                            }}>
                              {playerStatus === 'ACTIVE' ? 'Active' : 
                               playerStatus === 'ON_TRANSFER_LIST' ? 'On Transfer List' : 'Unknown'}
                            </span>
                          );
                        })()}
                      </td>
                      <td style={{
                        padding: spacing.md,
                        fontSize: typography.sm,
                        textAlign: 'center'
                      }}>
                        {(() => {
                          const playerStatus = playerStatuses[player.id] || 'ACTIVE';
                          if (editingPlayer === player.id) {
                            return (
                              <div style={{ display: 'flex', gap: '6px', justifyContent: 'center' }}>
                                <button
                                  onClick={() => handlePlayerSave(player.id)}
                                  disabled={loading}
                                  style={{
                                    padding: '4px 8px',
                                    backgroundColor: loading ? '#9ca3af' : '#10b981',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '4px',
                                    fontSize: '11px',
                                    cursor: loading ? 'not-allowed' : 'pointer',
                                    transition: 'background-color 0.2s'
                                  }}
                                >
                                  {loading ? 'Saving...' : 'Save'}
                                </button>
                                <button
                                  onClick={handlePlayerCancel}
                                  style={{
                                    padding: '4px 8px',
                                    backgroundColor: '#6b7280',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '4px',
                                    fontSize: '11px',
                                    cursor: 'pointer',
                                    transition: 'background-color 0.2s'
                                  }}
                                >
                                  Cancel
                                </button>
                              </div>
                            );
                          } else if (playerStatus === 'ACTIVE') {
                            return (
                              <div style={{ display: 'flex', gap: '6px', justifyContent: 'center', flexWrap: 'wrap' }}>
                                <button
                                  onClick={() => handlePlayerEdit(player)}
                                  style={{
                                    padding: '4px 8px',
                                    backgroundColor: '#3b82f6',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '4px',
                                    fontSize: '11px',
                                    cursor: 'pointer',
                                    transition: 'background-color 0.2s'
                                  }}
                                  onMouseEnter={(e) => {
                                    e.currentTarget.style.backgroundColor = '#2563eb';
                                  }}
                                  onMouseLeave={(e) => {
                                    e.currentTarget.style.backgroundColor = '#3b82f6';
                                  }}
                                >
                                  Edit
                                </button>
                                <button
                                  onClick={() => handlePutOnTransferList(player)}
                                  style={{
                                    padding: '4px 8px',
                                    backgroundColor: '#f59e0b',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '4px',
                                    fontSize: '11px',
                                    cursor: 'pointer',
                                    transition: 'background-color 0.2s'
                                  }}
                                  onMouseEnter={(e) => {
                                    e.currentTarget.style.backgroundColor = '#d97706';
                                  }}
                                  onMouseLeave={(e) => {
                                    e.currentTarget.style.backgroundColor = '#f59e0b';
                                  }}
                                >
                                  Transfer
                                </button>
                              </div>
                            );
                          } else {
                            return (
                              <div style={{ display: 'flex', gap: '6px', justifyContent: 'center', flexWrap: 'wrap' }}>
                                <button
                                  onClick={() => handlePlayerEdit(player)}
                                  style={{
                                    padding: '4px 8px',
                                    backgroundColor: '#3b82f6',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '4px',
                                    fontSize: '11px',
                                    cursor: 'pointer',
                                    transition: 'background-color 0.2s'
                                  }}
                                  onMouseEnter={(e) => {
                                    e.currentTarget.style.backgroundColor = '#2563eb';
                                  }}
                                  onMouseLeave={(e) => {
                                    e.currentTarget.style.backgroundColor = '#3b82f6';
                                  }}
                                >
                                  Edit
                                </button>
                                <button
                                  onClick={() => handleEditTransferPrice(player)}
                                  style={{
                                    padding: '4px 8px',
                                    backgroundColor: '#3b82f6',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '4px',
                                    fontSize: '11px',
                                    cursor: 'pointer',
                                    transition: 'background-color 0.2s'
                                  }}
                                  onMouseEnter={(e) => {
                                    e.currentTarget.style.backgroundColor = '#2563eb';
                                  }}
                                  onMouseLeave={(e) => {
                                    e.currentTarget.style.backgroundColor = '#3b82f6';
                                  }}
                                >
                                  Edit Price
                                </button>
                                <button
                                  onClick={() => handleRemoveFromTransferList(player)}
                                  disabled={removingPlayer === player.id}
                                  style={{
                                    padding: '4px 8px',
                                    backgroundColor: removingPlayer === player.id ? '#9ca3af' : '#ef4444',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '4px',
                                    fontSize: '11px',
                                    cursor: removingPlayer === player.id ? 'not-allowed' : 'pointer',
                                    transition: 'background-color 0.2s'
                                  }}
                                  onMouseEnter={(e) => {
                                    if (removingPlayer !== player.id) {
                                      e.currentTarget.style.backgroundColor = '#dc2626';
                                    }
                                  }}
                                  onMouseLeave={(e) => {
                                    e.currentTarget.style.backgroundColor = removingPlayer === player.id ? '#9ca3af' : '#ef4444';
                                  }}
                                >
                                  {removingPlayer === player.id ? 'Removing...' : 'Remove'}
                                </button>
                              </div>
                            );
                          }
                        })()}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            <div style={{
              padding: spacing.md,
              backgroundColor: colors.background,
              borderTop: `2px solid ${colors.border}`,
              borderBottom: `1px solid ${colors.border}`,
              borderRight: `1px solid ${colors.border}`,
              borderLeft: `1px solid ${colors.border}`,
              borderRadius: `0 0 ${borderRadius.md} ${borderRadius.md}`,
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center'
            }}>
              <span style={{
                fontSize: typography.sm,
                fontWeight: '600',
                color: colors.text.primary
              }}>
                Total Players
              </span>
              <span style={{
                fontSize: typography.sm,
                fontWeight: '600',
                color: colors.primary
              }}>
                {sortedPlayers.length}
              </span>
            </div>
          </>
        )}
      </div>

      {showTransferModal && selectedPlayer && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: 'rgba(0, 0, 0, 0.5)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000
        }}>
          <div style={{
            backgroundColor: 'white',
            padding: spacing.lg,
            borderRadius: borderRadius.md,
            width: '400px',
            maxWidth: '90vw'
          }}>
            <h3 style={{
              fontSize: typography.lg,
              fontWeight: '600',
              color: colors.text.primary,
              marginBottom: spacing.md
            }}>
              {isEditMode ? 'Edit Transfer Price' : 'Put Player on Transfer List'}
            </h3>

            <div style={{ marginBottom: spacing.md }}>
              <p style={{
                fontSize: typography.sm,
                color: colors.text.secondary,
                marginBottom: spacing.sm
              }}>
                Player: <strong>{selectedPlayer.firstName} {selectedPlayer.lastName}</strong>
              </p>
              <p style={{
                fontSize: typography.sm,
                color: colors.text.secondary,
                marginBottom: spacing.sm
              }}>
                Position: {selectedPlayer.position}  Age: {selectedPlayer.age} years
              </p>
              <p style={{
                fontSize: typography.sm,
                color: colors.text.secondary,
                marginBottom: spacing.sm
              }}>
                Current Market Value: <CurrencyDisplay amount={selectedPlayer.marketValue} />
              </p>
            </div>

            <div style={{ marginBottom: spacing.md }}>
              <label style={{
                display: 'block',
                fontSize: typography.sm,
                fontWeight: '500',
                color: colors.text.primary,
                marginBottom: spacing.xs
              }}>
                Ask Price ($):
              </label>
              <input
                type="number"
                value={askPrice}
                onChange={(e) => setAskPrice(e.target.value)}
                style={{
                  width: '100%',
                  padding: spacing.sm,
                  border: `1px solid ${colors.border}`,
                  borderRadius: borderRadius.sm,
                  fontSize: typography.sm
                }}
                min="0"
                step="1000"
              />
            </div>

            {error && (
              <div style={{
                padding: spacing.sm,
                backgroundColor: '#fef2f2',
                border: '1px solid #fecaca',
                borderRadius: borderRadius.sm,
                marginBottom: spacing.md,
                fontSize: typography.sm,
                color: '#dc2626'
              }}>
                {error}
              </div>
            )}

            <div style={{
              display: 'flex',
              gap: spacing.sm,
              justifyContent: 'flex-end'
            }}>
              <button
                onClick={handleCancelTransfer}
                disabled={transferingPlayer === selectedPlayer.id}
                style={{
                  padding: spacing.sm + ' ' + spacing.md,
                  backgroundColor: '#e5e7eb',
                  color: colors.text.primary,
                  border: 'none',
                  borderRadius: borderRadius.sm,
                  fontSize: typography.sm,
                  cursor: transferingPlayer === selectedPlayer.id ? 'not-allowed' : 'pointer',
                  opacity: transferingPlayer === selectedPlayer.id ? 0.5 : 1
                }}
              >
                Cancel
              </button>
              <button
                onClick={handleCreateTransfer}
                disabled={transferingPlayer === selectedPlayer.id || !askPrice}
                style={{
                  padding: spacing.sm + ' ' + spacing.md,
                  backgroundColor: transferingPlayer === selectedPlayer.id || !askPrice ? '#9ca3af' : '#f59e0b',
                  color: 'white',
                  border: 'none',
                  borderRadius: borderRadius.sm,
                  fontSize: typography.sm,
                  cursor: transferingPlayer === selectedPlayer.id || !askPrice ? 'not-allowed' : 'pointer'
                }}
              >
                {transferingPlayer === selectedPlayer.id ? 'Processing...' : (isEditMode ? 'Update Price' : 'Put on Transfer List')}
              </button>
            </div>
          </div>
        </div>
      )}

      {showRemoveConfirm && playerToRemove && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: 'rgba(0, 0, 0, 0.5)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000
        }}>
          <div style={{
            backgroundColor: 'white',
            padding: spacing.lg,
            borderRadius: borderRadius.md,
            width: '400px',
            maxWidth: '90vw'
          }}>
            <h3 style={{
              fontSize: typography.lg,
              fontWeight: '600',
              color: colors.text.primary,
              marginBottom: spacing.md
            }}>
              Remove from Transfer List
            </h3>

            <div style={{ marginBottom: spacing.md }}>
              <p style={{
                fontSize: typography.sm,
                color: colors.text.secondary,
                marginBottom: spacing.sm
              }}>
                Are you sure you want to remove <strong>{playerToRemove.firstName} {playerToRemove.lastName}</strong> from the transfer list?
              </p>
              <p style={{
                fontSize: typography.sm,
                color: colors.text.secondary,
                marginBottom: spacing.sm
              }}>
                Position: {playerToRemove.position}  Age: {playerToRemove.age} years
              </p>
              <p style={{
                fontSize: typography.sm,
                color: colors.text.secondary,
                marginBottom: spacing.sm
              }}>
                This player will no longer be available for other teams to buy.
              </p>
            </div>

            {error && (
              <div style={{
                padding: spacing.sm,
                backgroundColor: '#fef2f2',
                border: '1px solid #fecaca',
                borderRadius: borderRadius.sm,
                marginBottom: spacing.md,
                fontSize: typography.sm,
                color: '#dc2626'
              }}>
                {error}
              </div>
            )}

            <div style={{
              display: 'flex',
              gap: spacing.sm,
              justifyContent: 'flex-end'
            }}>
              <button
                onClick={cancelRemoveTransfer}
                disabled={removingPlayer === playerToRemove.id}
                style={{
                  padding: spacing.sm + ' ' + spacing.md,
                  backgroundColor: '#e5e7eb',
                  color: colors.text.primary,
                  border: 'none',
                  borderRadius: borderRadius.sm,
                  fontSize: typography.sm,
                  cursor: removingPlayer === playerToRemove.id ? 'not-allowed' : 'pointer',
                  opacity: removingPlayer === playerToRemove.id ? 0.5 : 1
                }}
              >
                Cancel
              </button>
              <button
                onClick={confirmRemoveTransfer}
                disabled={removingPlayer === playerToRemove.id}
                style={{
                  padding: spacing.sm + ' ' + spacing.md,
                  backgroundColor: removingPlayer === playerToRemove.id ? '#9ca3af' : '#ef4444',
                  color: 'white',
                  border: 'none',
                  borderRadius: borderRadius.sm,
                  fontSize: typography.sm,
                  cursor: removingPlayer === playerToRemove.id ? 'not-allowed' : 'pointer'
                }}
              >
                {removingPlayer === playerToRemove.id ? 'Removing...' : 'Remove'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
