import React, { useState, useEffect, useMemo } from 'react';
import { Team, Transfer } from '../types';
import { transferAPI } from '../services/api';
import { CurrencyDisplay } from './CurrencyDisplay';
import { cardStyle, flexRow } from '../styles/common';
import { colors, typography, spacing, borderRadius } from '../styles/theme';

interface SortConfig {
  key: keyof Team['players'][0];
  direction: 'asc' | 'desc';
}

interface DashboardProps {
  team: Team;
}

export const Dashboard: React.FC<DashboardProps> = ({ team }) => {
  const [sortConfig, setSortConfig] = useState<SortConfig>({
    key: 'position',
    direction: 'asc'
  });
  const [transfers, setTransfers] = useState<Transfer[]>([]);

  const positionOrder: Record<string, number> = {
    'GOALKEEPER': 0,
    'DEFENDER': 1,
    'MIDFIELDER': 2,
    'ATTACKER': 3,
    'goalkeeper': 0,
    'defender': 1,
    'midfielder': 2,
    'attacker': 3
  };

  useEffect(() => {
    const fetchTransfers = async () => {
      try {
        const transfersData = await transferAPI.getTransfers();
        setTransfers(transfersData);
      } catch (err) {
        console.error('Failed to fetch transfers:', err);
      }
    };
    fetchTransfers();
  }, []);

  // Determine player status by checking if they're on transfer list
  const getPlayerStatus = (playerId: string) => {
    const isOnTransferList = transfers.some(transfer => transfer.player.id === playerId);
    return isOnTransferList ? 'ON_TRANSFER_LIST' : 'ACTIVE';
  };

  const stats = [
    {
      label: 'Team Name',
      value: team.name,
      format: 'text'
    },
    {
      label: 'Team Value',
      value: parseFloat(team.marketValue || '0'),
      format: 'currency'
    },
    {
      label: 'Budget',
      value: parseFloat(team.budget || '0'),
      format: 'currency'
    }
  ];

  const sortedPlayers = useMemo(() => {
    if (!team.players) return [];

    const sorted = [...team.players].sort((a, b) => {
      // Special handling for position sorting
      if (sortConfig.key === 'position') {
        const aPositionOrder = positionOrder[a.position] ?? positionOrder[a.position?.toLowerCase()] ?? 999;
        const bPositionOrder = positionOrder[b.position] ?? positionOrder[b.position?.toLowerCase()] ?? 999;

        return sortConfig.direction === 'asc' 
          ? aPositionOrder - bPositionOrder
          : bPositionOrder - aPositionOrder;
      }

      // Regular sorting for other fields
      const aValue = a[sortConfig.key] ?? '';
      const bValue = b[sortConfig.key] ?? '';

      if (aValue < bValue) {
        return sortConfig.direction === 'asc' ? -1 : 1;
      }
      if (aValue > bValue) {
        return sortConfig.direction === 'asc' ? 1 : -1;
      }
      return 0;
    });

    return sorted;
  }, [team.players, sortConfig, positionOrder]);

  const handleSort = (key: keyof Team['players'][0]) => {
    setSortConfig(current => ({
      key,
      direction: current.key === key && current.direction === 'asc' ? 'desc' : 'asc'
    }));
  };

  const SortableHeader: React.FC<{ 
    children: React.ReactNode; 
    sortKey: keyof Team['players'][0];
    label: string;
  }> = ({ children, sortKey, label }) => (
    <button
      onClick={() => handleSort(sortKey)}
      style={{
        background: 'none',
        border: 'none',
        color: colors.text.primary,
        fontSize: typography.sm,
        fontWeight: '500',
        cursor: 'pointer',
        padding: spacing.xs,
        borderRadius: borderRadius.sm,
        display: 'flex',
        alignItems: 'center',
        gap: spacing.xs,
        transition: 'background-color 0.2s'
      }}
      onMouseEnter={(e) => {
        e.currentTarget.style.backgroundColor = colors.border;
      }}
      onMouseLeave={(e) => {
        e.currentTarget.style.backgroundColor = 'transparent';
      }}
    >
      {label}
      {sortConfig.key === sortKey && (
        <span style={{ fontSize: '10px' }}>
          {sortConfig.direction === 'asc' ? ' ↑' : ' ↓'}
        </span>
      )}
    </button>
  );

  return (
    <div style={{ display: 'flex', flexDirection: 'column', gap: spacing.lg }}>
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
        gap: spacing.lg
      }}>
        {stats.map((stat, index) => (
          <div key={index} style={{
            ...cardStyle,
            padding: spacing.lg
          }}>
            <h3 style={{
              fontSize: typography.sm,
              fontWeight: '500',
              color: colors.text.secondary,
              marginBottom: spacing.sm
            }}>
              {stat.label}
            </h3>
            <p style={{
              fontSize: typography.lg,
              fontWeight: '600',
              color: colors.text.primary
            }}>
              {stat.format === 'currency' ? (
                <CurrencyDisplay amount={stat.value} />
              ) : (
                stat.value
              )}
            </p>
          </div>
        ))}
      </div>

      <div style={cardStyle}>
        <div style={{
          padding: spacing.lg,
          borderBottom: `1px solid ${colors.border}`
        }}>
          <h3 style={{
            fontSize: typography.lg,
            fontWeight: '600',
            color: colors.text.primary,
            marginBottom: spacing.xs
          }}>
            Players
          </h3>
          <p style={{
            fontSize: typography.sm,
            color: colors.text.secondary
          }}>
            View your team players and their details.
          </p>
        </div>

        {sortedPlayers.length === 0 ? (
          <div style={{
            padding: spacing.xl,
            textAlign: 'center',
            color: colors.text.secondary
          }}>
            No players found
          </div>
        ) : (
          <>
            <div style={{
              overflowX: 'auto',
              border: `1px solid ${colors.border}`,
              borderRadius: borderRadius.md
            }}>
              <table style={{
                width: '100%',
                borderCollapse: 'collapse',
                backgroundColor: colors.surface
              }}>
                <thead>
                  <tr style={{
                    backgroundColor: colors.background,
                    borderBottom: `2px solid ${colors.border}`
                  }}>
                    <th style={{
                      padding: spacing.md,
                      textAlign: 'left',
                      fontSize: typography.sm,
                      fontWeight: '600',
                      color: colors.text.primary,
                      borderRight: `1px solid ${colors.border}`
                    }}>
                      <SortableHeader sortKey="firstName" label="Name">Name</SortableHeader>
                    </th>
                    <th style={{
                      padding: spacing.md,
                      textAlign: 'left',
                      fontSize: typography.sm,
                      fontWeight: '600',
                      color: colors.text.primary,
                      borderRight: `1px solid ${colors.border}`
                    }}>
                      <SortableHeader sortKey="position" label="Position">Position</SortableHeader>
                    </th>
                    <th style={{
                      padding: spacing.md,
                      textAlign: 'left',
                      fontSize: typography.sm,
                      fontWeight: '600',
                      color: colors.text.primary,
                      borderRight: `1px solid ${colors.border}`
                    }}>
                      <SortableHeader sortKey="age" label="Age">Age</SortableHeader>
                    </th>
                    <th style={{
                      padding: spacing.md,
                      textAlign: 'left',
                      fontSize: typography.sm,
                      fontWeight: '600',
                      color: colors.text.primary,
                      borderRight: `1px solid ${colors.border}`
                    }}>
                      <SortableHeader sortKey="country" label="Country">Country</SortableHeader>
                    </th>
                    <th style={{
                      padding: spacing.md,
                      textAlign: 'left',
                      fontSize: typography.sm,
                      fontWeight: '600',
                      color: colors.text.primary
                    }}>
                      <SortableHeader sortKey="marketValue" label="Market Value">Market Value</SortableHeader>
                    </th>
                    <th style={{
                      padding: spacing.md,
                      textAlign: 'center',
                      fontSize: typography.sm,
                      fontWeight: '600',
                      color: colors.text.primary
                    }}>
                      Status
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {sortedPlayers.map((player, index) => (
                    <tr key={player.id} style={{
                      backgroundColor: index % 2 === 0 ? colors.surface : colors.background,
                      borderBottom: `1px solid ${colors.border}`,
                      transition: 'background-color 0.2s'
                    }}>
                      <td style={{
                        padding: spacing.md,
                        fontSize: typography.sm,
                        color: colors.text.primary,
                        borderRight: `1px solid ${colors.border}`
                      }}>
                        <div>
                          <div style={{ fontWeight: '500' }}>
                            {player.firstName} {player.lastName}
                          </div>
                        </div>
                      </td>
                      <td style={{
                        padding: spacing.md,
                        fontSize: typography.sm,
                        color: colors.text.secondary,
                        borderRight: `1px solid ${colors.border}`
                      }}>
                        {player.position}
                      </td>
                      <td style={{
                        padding: spacing.md,
                        fontSize: typography.sm,
                        color: colors.text.secondary,
                        borderRight: `1px solid ${colors.border}`
                      }}>
                        {player.age} years
                      </td>
                      <td style={{
                        padding: spacing.md,
                        fontSize: typography.sm,
                        color: colors.text.secondary,
                        borderRight: `1px solid ${colors.border}`
                      }}>
                        {player.country}
                      </td>
                      <td style={{
                        padding: spacing.md,
                        fontSize: typography.sm,
                        color: colors.text.primary,
                        borderRight: `1px solid ${colors.border}`
                      }}>
                        <CurrencyDisplay amount={player.marketValue} />
                      </td>
                      <td style={{
                        padding: spacing.md,
                        fontSize: typography.sm,
                        textAlign: 'center'
                      }}>
                        {(() => {
                          const playerStatus = getPlayerStatus(player.id);
                          return (
                            <span style={{
                              padding: '2px 8px',
                              fontSize: '12px',
                              fontWeight: '500',
                              borderRadius: '9999px',
                              backgroundColor: playerStatus === 'ACTIVE' ? '#dcfce7' : 
                                             playerStatus === 'ON_TRANSFER_LIST' ? '#fef3c7' : '#e5e7eb',
                              color: playerStatus === 'ACTIVE' ? '#166534' : 
                                      playerStatus === 'ON_TRANSFER_LIST' ? '#92400e' : '#6b7280'
                            }}>
                              {playerStatus === 'ACTIVE' ? 'Active' : 
                               playerStatus === 'ON_TRANSFER_LIST' ? 'On Transfer List' : 'Unknown'}
                            </span>
                          );
                        })()}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            <div style={{
              padding: spacing.md,
              backgroundColor: colors.background,
              borderTop: `2px solid ${colors.border}`,
              borderBottom: `1px solid ${colors.border}`,
              borderRight: `1px solid ${colors.border}`,
              borderLeft: `1px solid ${colors.border}`,
              borderRadius: `0 0 ${borderRadius.md} ${borderRadius.md}`,
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center'
            }}>
              <span style={{
                fontSize: typography.sm,
                fontWeight: '600',
                color: colors.text.primary
              }}>
                Total Players
              </span>
              <span style={{
                fontSize: typography.sm,
                fontWeight: '600',
                color: colors.primary
              }}>
                {sortedPlayers.length}
              </span>
            </div>
          </>
        )}
      </div>
    </div>
  );
};
